import React from "react";
// import "./style.css";
// import animal from "../../src/animals.json";






function CharCard(props) {
  return (

    <React.Fragment>
      {/* Inserting props.id as an argument for the RemoveChar function, so we can specifically tell it which ID to be removing, i.e. the thing we just clicked on */}

    <div className="animalCard">
        <button src="https://cdn3.iconfinder.com/data/icons/user-interface-48/52/empty-star-512.png" onClick={() => props.addFavorite(props.id)}/>


        <div className="img-container">
          <img alt={props.name} src={props.image} />
        </div>

        <div className="content">
          <ul>
            <li>
                <strong>Name:</strong> {props.animal.name}
            </li>
            <li>
                <strong>Type:</strong> {props.animal.type}
            </li>
            <li>
                <strong>Gender:</strong> {props.animal.gender}
            </li>
            <li>
                <strong>Description:</strong> {props.animal.description}
            </li>
            <li>
                <strong>Notes:</strong>
                <ul>
                <li>
                    <strong>Spayed/Neutrered:</strong> {props.animal.spayed_neutered}
                </li>
                <li>
                    <strong>Up to Date on Shots:</strong> {props.animal.shots_current}
                </li>
                <li>
                    <strong>Child-Friendly:</strong> {props.animal.children}
                </li>
                
                </ul>
            </li>
            <ul>
                <li>
                    <strong>Contact Info:</strong>
                </li>
                <li>
                    <strong>Email:</strong> {props.animal.email}
                </li>
                <li>
                    <strong>Phone Number:</strong> {props.animal.phone}
                </li>
            </ul>
          </ul>        
        </div>

        {/* <span onClick={() => props.removeChar(props.id)} className="remove">ùòÖ</span> */}
    </div>
    </React.Fragment>
  );
}

export default CharCard;
